;; **
;;; # Loom test
;;; 
;;; A worksheet to test out Gorilla rendering for loom.
;; **

;; @@
(ns loom-test
  (:use [loom graph io])
  (:use loom-gorilla.render)
  (:use [gorilla-repl table latex]))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-nil'>nil</span>","value":"nil"}
;; <=

;; **
;;; We'll make up random directed graphs. This function makes a random node, with symbol `:0` to `:3`
;; **

;; @@
(defn r-node [] (keyword (str (rand-int 4))))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;loom-test/r-node</span>","value":"#'loom-test/r-node"}
;; <=

;; **
;;; And a random weighted edge.
;; **

;; @@
(defn r-edge [] [(r-node) (r-node) (rand-int 10)])
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;loom-test/r-edge</span>","value":"#'loom-test/r-edge"}
;; <=

;; **
;;; Put it together into a graph.
;; **

;; @@
(defn r-wdg [] (apply weighted-digraph (repeatedly 5 r-edge)))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;loom-test/r-wdg</span>","value":"#'loom-test/r-wdg"}
;; <=

;; @@
(r-wdg)
